import React, { Component } from 'react'

export default class DashBannerActiveBox extends Component {
    constructor(props) {
        super(props);
        this.state = {
          selectedLanguage: <img src={require('../../../assets/women-fas-cat-7.png')} alt=""/>
        };
        
        this.updateLanguage = this.updateLanguage.bind(this);
      }
      updateLanguage(lang) {
        this.setState(function() {
          return {
            selectedLanguage: lang
          }
        });
      }
        
      render() {
        var languages = [<img src={require('../../../assets/women-fas-cat-7.png')} alt=""/>, <img src={require('../../../assets/women-fas-cat-8.png')} alt=""/>, <img src={require('../../../assets/women-fas-cat-6.png')} alt=""/>];
    return (
      <>
      <div className="dash-banner-active-box">
        <ul>
            <p>Selected Item: {this.state.selectedLanguage}</p>
            {languages.map(function(lang) {
            return <li
                    style={lang === this.state.selectedLanguage ? {color: 'red', border: '1px solid red',} : null}
                    onClick = {this.updateLanguage.bind(null, lang)}
                    >{lang}</li> 
            },this)}
        </ul>
      </div>
      </>
    )
  }
}
