import React, { Component } from 'react'
import DashboardSidebar from '../dashboardsidebar/DashboardSidebar';
import DashboardTopBar from '../dashboardtopbar/DashboardTopBar';
import './dashallproduct.css'

import RichTextEditor from 'react-rte';

export default class DashAddProduct extends Component {

    constructor(props) {
		super(props);
		
		this.state = {
            // For Text Editor
            value: RichTextEditor.createEmptyValue(),
            // For Text Editor
			files: [],
			urls: [],
			isDragging: false
		}
		
		// this.onChange = this.onChange.bind(this);
		this.handleDrop = this.handleDrop.bind(this);
		this.handleDragEnter = this.handleDragEnter.bind(this);
		this.handleDragOver = this.handleDragOver.bind(this);
		this.handleDragLeave = this.handleDragLeave.bind(this);
		this.handleFiles = this.handleFiles.bind(this);
		this.onRemove = this.onRemove.bind(this);
	}
     
    //   state = {
    //     value: RichTextEditor.createEmptyValue()
    //   }
     
    // For Text Editor
      onChange = (value) => {
        this.setState({value});
      };
      // For Text Editor

      onRemove(index) {
		var {files, urls} = this.state;
		let newFiles = files.filter((file, i) => i !== index);
		let newUrls = urls.filter((url, i) => i !== index);
		
		this.setState({
			...this.state,
			files: newFiles,
			urls: newUrls
		});
	}
	
	handleDrags(e) {
		e.preventDefault();
		e.stopPropagation();
		
		this.setState({
			...this.state,
			isDragging: true
		});
	}
	
	handleDragEnter(e) {
		this.handleDrags(e);
	}
	
	handleDragOver(e) {
		this.handleDrags(e);
	}
	
	handleDragLeave(e) {
		e.preventDefault();
		e.stopPropagation();
		
		this.setState({
			...this.state,
			isDragging: false
		});
	}
	
	onChange(e) {
		e.preventDefault()
		const files = e.target.files;
		[].forEach.call(files, this.handleFiles);
	}
	
	handleDrop(e) {
		e.stopPropagation();
		e.preventDefault();
		
		const data = e.dataTransfer;
		const files = data.files;
		console.log("Oops...you dropped this: ", files);
		
		[].forEach.call(files, this.handleFiles);
		
		this.setState({
			...this.state,
			isDragging: false
		});
	}
	
	handleFiles(file) {
		
		// this could be refactored to not use the file reader
		
		var reader = new FileReader();
		
		reader.onloadend = () => {

			var imageUrl = window.URL.createObjectURL(file);
			
			this.setState({
				files: [file, ...this.state.files],
				urls: [imageUrl, ...this.state.urls]
			});
			
		}
		
		reader.readAsDataURL(file);
	}
      
    render() {
        // For Image Upload
        const {urls, files, isDragging} = this.state;
		const dropClass = isDragging ? "dragDrop dragging" : "dragDrop";
        // For Image Upload
        return (
            <>
            <div className="example-sasjghdjhgs">
                <div className="container-fluid">
                    <div className="row obd-top-bar-main-order-main-dashboard-ex-pdd">
                        <div className="col-md-2">
                            <DashboardSidebar/>
                        </div>
                        <div className="col-md-10">
                          {/* Top Search bar and profile sec start */}
                          <div className="row">
                            <div className="col-md-12">
                              <div className="obd-top-bar-main-order-dash-board-top-search-boxx">
                                <DashboardTopBar/>
                              </div>
                            </div>
                          </div>
                          {/* Top Search bar and profile sec end ---------- */}

                          {/* Add Product Box Start */}
                          <div className="obd-recent-order-bx-product-and-new-user-dash-board-main-box-addp">
                            <div className="row">
                                <div className="col-md-12">
                                    <div className="obd-recent-order-bx-product-and-new-user-dash-board-main-box-content">
                                        {/* Head sec start */}
                                        <div className="orponbd-recent-order-bx-color-section-box">
                                            <div className="orponbd-recent-order-color-sectionn-paddv">
                                                <div className="orponbd-recent-order-bx-txtt-hd">
                                                    <h4>Add Product</h4>
                                                    <div className="flltrr-bbttn-brdr-recent-orderxv"></div>
                                                </div>
                                            </div>
                                        </div>
                                        {/* Head sec end */}

                                        {/* Add Product Field Box */}
                                        <form action="">
                                            <div className="obd-preorder-add-product-bx-main-wrap">
                                                {/* Single Item Start */}
                                                <div className="row obd-preorder-add-product-bx-sng-ex-pdz">
                                                    <div className="col-md-2">
                                                        <div className="obd-preorder-add-product-bx-lft-hdtxt">
                                                            <h4>Title :</h4>
                                                        </div>
                                                    </div>
                                                    <div className="col-md-10">
                                                        <div className="obd-preorder-add-product-bx-rt-inpt-fld">
                                                            <input type="text" placeholder="Enter Title"/>
                                                        </div>
                                                    </div>
                                                </div>
                                                {/* Single Item End */}
                                                {/* Single Item Start */}
                                                <div className="row obd-preorder-add-product-bx-sng-ex-pdz">
                                                    <div className="col-md-2">
                                                        <div className="obd-preorder-add-product-bx-lft-hdtxt">
                                                            <h4>Price :</h4>
                                                        </div>
                                                    </div>
                                                    <div className="col-md-10">
                                                        <div className="obd-preorder-add-product-bx-rt-inpt-fld">
                                                            <div className="obd-preorder-add-product-bx-rt-inpt-prc">
                                                                <ul>
                                                                    <li>
                                                                        <label htmlFor="">Selling Price</label>
                                                                        <input type="number" placeholder="1200 TK" />
                                                                    </li>
                                                                    <li>
                                                                        <label htmlFor="">Discount Price</label>
                                                                        <input type="number" placeholder="950 TK" />
                                                                    </li>
                                                                </ul>
                                                            </div>
                                                        </div>
                                                    </div>
                                                </div>
                                                {/* Single Item End */}
                                                {/* Single Item Start */}
                                                <div className="row obd-preorder-add-product-bx-sng-ex-pdz">
                                                    <div className="col-md-2">
                                                        <div className="obd-preorder-add-product-bx-lft-hdtxt">
                                                            <h4>Description :</h4>
                                                        </div>
                                                    </div>
                                                    <div className="col-md-10">
                                                        <div className="obd-preorder-add-product-bx-rt-inpt-desc">
                                                            <RichTextEditor
                                                                value={this.state.value}
                                                                onChange={this.onChange}
                                                            />
                                                        </div>
                                                    </div>
                                                </div>
                                                {/* Single Item End */}
                                                {/* Single Item Start */}
                                                <div className="row obd-preorder-add-product-bx-sng-ex-pdz">
                                                    <div className="col-md-2">
                                                        <div className="obd-preorder-add-product-bx-lft-hdtxt">
                                                            <h4>Product Image :</h4>
                                                        </div>
                                                    </div>
                                                    <div className="col-md-10">
                                                        <div className="obd-preorder-add-product-bx-rt-inpt-fld">
                                                            {/* <input type="file" placeholder="Enter Title" multiple/> */}
                                                            <div>
				<div className="uploadInput" >
					<input type="file"
						onChange={this.onChange}
						accept="image/*"
						multiple
					/>
					<div className={dropClass} 
						onDrop={this.handleDrop}
						onDragOver={this.handleDragOver}
						onDragEnter={this.handleDragEnter}
						onDragLeave={this.handleDragLeave} >
						<div className="inside">
							<span>Drop files here</span>
							<div>
								<i className="material-icons">file_upload</i>
							</div>
						</div>
					</div>	
				</div>
				<div className="imagePreviewContainer">
					{
						urls && (urls.map((url, i) => (
							<div className="previewItem">
								<img className="imagePreview" src={url} />
								<div className="details">KBs</div>
									<i className="material-icons" 
									onClick={() => this.onRemove(i)}>delete</i>
								</div>
						)))
					}
				</div>
				</div>
                                                        </div>
                                                    </div>
                                                </div>
                                                {/* Single Item End */}

                                                {/* Save Button */}
                                                <div className="obd-add-prod-save-bbtn text-center">
                                                    {/* <div className="row"> */}
                                                        <button type="button">Save</button>
                                                    {/* </div> */}
                                                </div>
                                                {/* Save Button */}
                                            </div>
                                        </form>
                                        {/* Add Product Field Box End */}
                                    </div>
                                </div>
                                </div>
                            </div>
                          {/* Add Product Box End */}
                        </div>
                    </div>
                </div>
            </div>
            </>
        )
    }
}
